#!/usr/bin/env node

const options = {
  port: {
    alias: 'p',
    desc: 'Listen port',
    default: 8000
  },
  host: {
    alias: 'h',
    desc: 'Server address',
    default: '127.0.0.1'
  },
  env: {
    alias: 'e',
    desc: 'Server environment',
    default: process.env.NODE_ENV ? process.env.NODE_ENV : 'development'
  },
  static: {
    alias: 's',
    desc: 'Server static files from paths',
    type: 'array',
  }
};

const Server = require('../lib/');
const argv = require('yargs').options(options).argv;

new Server(argv);
