const express = require('express');
const fs = require('fs');

module.exports = (paths = [], app, server, logger) => {
  paths.map(path => {
    try {
      fs.statSync(path);
      app.use(express.static(path));
      logger.info('serving static files from:', path);
    }
    catch (err) {
      logger.error('serving folder not found:', path);
    }
  });
};
